import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({
    title: "",
    amount: "",
    category: "",
    type: "income",
  });

  const [summary, setSummary] = useState({ income: 0, expense: 0 });

  // Fetch transactions from backend
  const fetchTransactions = async () => {
    try {
      const res = await axios.get("http://localhost:5000/transactions");
      setTransactions(res.data);
      calculateSummary(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTransactions();
  }, []);

  const calculateSummary = (data) => {
    const income = data
      .filter((t) => t.type === "income")
      .reduce((sum, t) => sum + parseFloat(t.amount), 0);
    const expense = data
      .filter((t) => t.type === "expense")
      .reduce((sum, t) => sum + parseFloat(t.amount), 0);
    setSummary({ income, expense });
  };

  // Add transaction
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/transactions", form);
      setForm({ title: "", amount: "", category: "", type: "income" });
      fetchTransactions();
    } catch (err) {
      console.error(err);
    }
  };

  // Delete transaction
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/transactions/${id}`);
      fetchTransactions();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="App">
      <header className="header">
        <h1>FinTrack: Personal Finance & Budget Manager</h1>
      </header>

      <section className="form-section">
        <h2>Add Transaction</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Title"
            value={form.title}
            onChange={(e) => setForm({ ...form, title: e.target.value })}
            required
          />
          <input
            type="number"
            placeholder="Amount"
            value={form.amount}
            onChange={(e) => setForm({ ...form, amount: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Category"
            value={form.category}
            onChange={(e) => setForm({ ...form, category: e.target.value })}
            required
          />
          <select
            value={form.type}
            onChange={(e) => setForm({ ...form, type: e.target.value })}
          >
            <option value="income">Income</option>
            <option value="expense">Expense</option>
          </select>
          <button type="submit">Add</button>
        </form>
      </section>

      <section className="table-section">
        <h2>Transactions</h2>
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Amount</th>
              <th>Category</th>
              <th>Type</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((t) => (
              <tr key={t.id}>
                <td>{t.title}</td>
                <td>{t.amount}</td>
                <td>{t.category}</td>
                <td>{t.type}</td>
                <td>
                  <button onClick={() => handleDelete(t.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="summary">
          <h3>Summary</h3>
          <p>Total Income: ${summary.income.toFixed(2)}</p>
          <p>Total Expenses: ${summary.expense.toFixed(2)}</p>
        </div>

        {/* Optional Chart placeholder */}
        <div className="chart-placeholder">
          <p>Transaction chart will go here (Chart.js can be added later)</p>
        </div>
      </section>

      <footer className="footer">
        <p>© 2025 FinTrack</p>
        <p>
          <a href="#">Live Demo</a> | <a href="#">GitHub Repo</a>
        </p>
      </footer>
    </div>
  );
}

export default App;
