import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
import Chart from "chart.js/auto";
import { Bar } from "react-chartjs-2";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({ title: "", amount: "", category: "", type: "income" });

  const fetchTransactions = async () => {
    try {
      const res = await axios.get("http://localhost:5000/transactions");
      setTransactions(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTransactions();
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/transactions", form);
      setForm({ title: "", amount: "", category: "", type: "income" });
      fetchTransactions();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/transactions/${id}`);
      fetchTransactions();
    } catch (err) {
      console.error(err);
    }
  };

  // Prepare data for chart
  const chartData = {
    labels: transactions.map((t) => t.title),
    datasets: [
      {
        label: "Amount",
        data: transactions.map((t) => t.amount),
        backgroundColor: transactions.map((t) => (t.type === "income" ? "#4caf50" : "#f44336")),
      },
    ],
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>MorganLedger</h1>
        <p>Personal Finance Tracker</p>
      </header>
      <main className="app-main">
        <section className="form-section">
          <h2>Add Transaction</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="title"
              placeholder="Title"
              value={form.title}
              onChange={handleChange}
              required
            />
            <input
              type="number"
              name="amount"
              placeholder="Amount"
              value={form.amount}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="category"
              placeholder="Category"
              value={form.category}
              onChange={handleChange}
              required
            />
            <select name="type" value={form.type} onChange={handleChange}>
              <option value="income">Income</option>
              <option value="expense">Expense</option>
            </select>
            <button type="submit">Add</button>
          </form>
        </section>

        <section className="transactions-section">
          <h2>Transactions</h2>
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Amount</th>
                <th>Category</th>
                <th>Type</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((t) => (
                <tr key={t.id}>
                  <td>{t.title}</td>
                  <td>{t.amount}</td>
                  <td>{t.category}</td>
                  <td>{t.type}</td>
                  <td>
                    <button onClick={() => handleDelete(t.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        <section className="chart-section">
          <h2>Transactions Chart</h2>
          <Bar data={chartData} />
        </section>
      </main>
    </div>
  );
}

export default App;
