          </div>
        </div>

        {/* Footer */}
        <footer
          style={{
            marginTop: "2rem",
            padding: "1rem",
            textAlign: "center",
            backgroundColor: "#002366",
            color: "white",
            borderRadius: "8px",
          }}
        >
          <p>Total Transactions: {transactions.length}</p>
          <p>Net Balance: ${balance.toFixed(2)}</p>
        </footer>
      </div>
    </div>
  );
};

export default App;
