import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [transactions, setTransactions] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    amount: "",
    category: "",
    type: "income",
  });

  const fetchTransactions = async () => {
    try {
      const res = await axios.get("http://localhost:5000/transactions");
      setTransactions(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTransactions();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/transactions", formData);
      setFormData({ title: "", amount: "", category: "", type: "income" });
      fetchTransactions();
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/transactions/${id}`);
      fetchTransactions();
    } catch (err) {
      console.error(err);
    }
  };

  // Summary calculations
  const totalIncome = transactions
    .filter((t) => t.type === "income")
    .reduce((sum, t) => sum + parseFloat(t.amount), 0);
  const totalExpenses = transactions
    .filter((t) => t.type === "expense")
    .reduce((sum, t) => sum + parseFloat(t.amount), 0);
  const balance = totalIncome - totalExpenses;

  return (
    <div style={{ display: "flex", minHeight: "100vh" }}>
      {/* Sidebar */}
      <div
        style={{
          width: "220px",
          backgroundColor: "#002366",
          color: "white",
          padding: "1rem",
        }}
      >
        <h2>FinTrack</h2>
        <nav style={{ marginTop: "2rem" }}>
          <p>Dashboard</p>
          <p>Add Transaction</p>
          <p>Transactions</p>
        </nav>
      </div>

      {/* Main content */}
      <div style={{ flex: 1, padding: "2rem", backgroundColor: "#f8f8f8" }}>
        {/* Header */}
        <header
          style={{
            textAlign: "center",
            marginBottom: "2rem",
          }}
        >
          <h1>FinTrack: Personal Finance & Budget Manager</h1>
          <p>Track your income, expenses, and budgets easily</p>
        </header>

        {/* Summary cards */}
        <div
          style={{
            display: "flex",
            gap: "1rem",
            marginBottom: "2rem",
            justifyContent: "center",
          }}
        >
          <div
            style={{
              flex: 1,
              padding: "1rem",
              backgroundColor: "#e0e0e0",
              borderRadius: "8px",
              textAlign: "center",
            }}
          >
            <h3>Total Income</h3>
            <p>${totalIncome.toFixed(2)}</p>
          </div>
          <div
            style={{
              flex: 1,
              padding: "1rem",
              backgroundColor: "#e0e0e0",
              borderRadius: "8px",
              textAlign: "center",
            }}
          >
            <h3>Total Expenses</h3>
            <p>${totalExpenses.toFixed(2)}</p>
          </div>
          <div
            style={{
              flex: 1,
              padding: "1rem",
              backgroundColor: "#e0e0e0",
              borderRadius: "8px",
              textAlign: "center",
            }}
          >
            <h3>Balance</h3>
            <p>${balance.toFixed(2)}</p>
          </div>
        </div>

        {/* Add transaction form */}
        <form
          onSubmit={handleSubmit}
          style={{
            display: "flex",
            gap: "1rem",
            marginBottom: "2rem",
            flexWrap: "wrap",
            justifyContent: "center",
          }}
        >
          <input
            type="text"
            name="title"
            placeholder="Title"
            value={formData.title}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="amount"
            placeholder="Amount"
            value={formData.amount}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="category"
            placeholder="Category"
            value={formData.category}
            onChange={handleChange}
            required
          />
          <select name="type" value={formData.type} onChange={handleChange}>
            <option value="income">Income</option>
            <option value="expense">Expense</option>
          </select>
          <button type="submit" style={{ backgroundColor: "#002366", color: "white", padding: "0.5rem 1rem" }}>
            Add Transaction
          </button>
        </form>

        {/* Transactions table */}
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            backgroundColor: "white",
          }}
        >
          <thead>
            <tr style={{ backgroundColor: "#002366", color: "white" }}>
              <th style={{ padding: "0.5rem" }}>Title</th>
              <th>Amount</th>
              <th>Category</th>
              <th>Type</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map((t) => (
              <tr key={t.id} style={{ textAlign: "center", borderBottom: "1px solid #ccc" }}>
                <td>{t.title}</td>
                <td>${parseFloat(t.amount).toFixed(2)}</td>
                <td>{t.category}</td>
                <td>{t.type}</td>
                <td>
                  <button
                    onClick={() => handleDelete(t.id)}
                    style={{ backgroundColor: "red", color: "white", padding: "0.3rem 0.5rem", border: "none", borderRadius: "4px" }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {transactions.length === 0 && (
              <tr>
                <td colSpan="5">No transactions yet</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
