import React, { useEffect, useState } from "react";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({
    title: "",
    amount: "",
    category: "",
    type: "income",
  });

  // Fetch all transactions
  const fetchTransactions = () => {
    fetch("http://localhost:5000/transactions")
      .then((res) => res.json())
      .then((data) => setTransactions(data))
      .catch((err) => console.error("Error fetching:", err));
  };

  useEffect(() => {
    fetchTransactions();
  }, []);

  // Add a transaction
  const addTransaction = (e) => {
    e.preventDefault();

    fetch("http://localhost:5000/transactions", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        title: form.title,
        amount: Number(form.amount),
        category: form.category,
        type: form.type,
      }),
    })
      .then((res) => res.json())
      .then(() => {
        setForm({ title: "", amount: "", category: "", type: "income" });
        fetchTransactions();
      })
      .catch((err) => console.error("Error adding:", err));
  };

  // Delete a transaction
  const deleteTransaction = (id) => {
    fetch(`http://localhost:5000/transactions/${id}`, { method: "DELETE" })
      .then(() => fetchTransactions())
      .catch((err) => console.error("Error deleting:", err));
  };

  const totalIncome = transactions
    .filter((t) => t.type === "income")
    .reduce((sum, t) => sum + t.amount, 0);
  const totalExpenses = transactions
    .filter((t) => t.type === "expense")
    .reduce((sum, t) => sum + t.amount, 0);
  const balance = totalIncome - totalExpenses;

  return (
    <div style={{ fontFamily: "Arial", padding: "30px", maxWidth: "800px", margin: "auto" }}>
      <h1 style={{ textAlign: "center", marginBottom: "30px" }}>💼 FinTrack: Personal Finance & Budget Manager</h1>

      <div style={{ display: "flex", justifyContent: "space-around", marginBottom: "40px" }}>
        <div>
          <h2>Income</h2>
          <p style={{ color: "green", fontSize: "20px" }}>+${totalIncome}</p>
        </div>
        <div>
          <h2>Expenses</h2>
          <p style={{ color: "red", fontSize: "20px" }}>-${totalExpenses}</p>
        </div>
        <div>
          <h2>Balance</h2>
          <p style={{ fontSize: "20px" }}>${balance}</p>
        </div>
      </div>

      <form onSubmit={addTransaction} style={{ marginBottom: "30px" }}>
        <h2>Add Transaction</h2>
        <input
          type="text"
          placeholder="Title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
          required
          style={{ padding: "8px", marginRight: "10px" }}
        />
        <input
          type="number"
          placeholder="Amount"
          value={form.amount}
          onChange={(e) => setForm({ ...form, amount: e.target.value })}
          required
          style={{ padding: "8px", marginRight: "10px" }}
        />
        <input
          type="text"
          placeholder="Category"
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
          style={{ padding: "8px", marginRight: "10px" }}
        />
        <select
          value={form.type}
          onChange={(e) => setForm({ ...form, type: e.target.value })}
          style={{ padding: "8px", marginRight: "10px" }}
        >
          <option value="income">Income</option>
          <option value="expense">Expense</option>
        </select>
        <button type="submit" style={{ padding: "8px 15px" }}>Add</button>
      </form>

      <h2>Transactions</h2>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr style={{ backgroundColor: "#f0f0f0" }}>
            <th style={{ border: "1px solid #ccc", padding: "10px" }}>Title</th>
            <th style={{ border: "1px solid #ccc", padding: "10px" }}>Amount</th>
            <th style={{ border: "1px solid #ccc", padding: "10px" }}>Category</th>
            <th style={{ border: "1px solid #ccc", padding: "10px" }}>Type</th>
            <th style={{ border: "1px solid #ccc", padding: "10px" }}>Action</th>
          </tr>
        </thead>
        <tbody>
          {transactions.length > 0 ? (
            transactions.map((t) => (
              <tr key={t.id}>
                <td style={{ border: "1px solid #ccc", padding: "10px" }}>{t.title}</td>
                <td style={{ border: "1px solid #ccc", padding: "10px" }}>${t.amount}</td>
                <td style={{ border: "1px solid #ccc", padding: "10px" }}>{t.category}</td>
                <td style={{ border: "1px solid #ccc", padding: "10px" }}>{t.type}</td>
                <td style={{ border: "1px solid #ccc", padding: "10px" }}>
                  <button
                    onClick={() => deleteTransaction(t.id)}
                    style={{ background: "red", color: "white", border: "none", padding: "5px 10px", cursor: "pointer" }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" style={{ textAlign: "center", padding: "20px" }}>
                No transactions yet
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default App;
