import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    amount: "",
    category: "",
    type: "income",
  });

  // Fetch transactions from backend
  useEffect(() => {
    fetch("http://localhost:5000/transactions")
      .then((res) => res.json())
      .then((data) => setTransactions(data))
      .catch((err) => console.error("Error fetching transactions:", err));
  }, []);

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add new transaction
  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/transactions", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    })
      .then((res) => res.json())
      .then((data) => {
        alert("Transaction added!");
        setTransactions([...transactions, { ...formData, id: data.id }]);
        setFormData({ title: "", amount: "", category: "", type: "income" });
      })
      .catch((err) => console.error("Error adding transaction:", err));
  };

  // Delete a transaction
  const handleDelete = (id) => {
    fetch(`http://localhost:5000/transactions/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        setTransactions(transactions.filter((t) => t.id !== id));
      })
      .catch((err) => console.error("Error deleting transaction:", err));
  };

  return (
    <div className="App">
      <h1>üí∞ Expense & Budget Tracker</h1>

      <form onSubmit={handleSubmit} className="form">
        <input
          name="title"
          placeholder="Title"
          value={formData.title}
          onChange={handleChange}
          required
        />
        <input
          name="amount"
          placeholder="Amount"
          type="number"
          value={formData.amount}
          onChange={handleChange}
          required
        />
        <input
          name="category"
          placeholder="Category"
          value={formData.category}
          onChange={handleChange}
          required
        />
        <select name="type" value={formData.type} onChange={handleChange}>
          <option value="income">Income</option>
          <option value="expense">Expense</option>
        </select>
        <button type="submit">Add Transaction</button>
      </form>

      <h2>üìä Transactions</h2>
      <ul>
        {transactions.map((t) => (
          <li key={t.id}>
            {t.title} ‚Äî ${t.amount} ({t.category}) [{t.type}]
            <button onClick={() => handleDelete(t.id)}>‚ùå Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
